    "experience = st.selectbox('Select your experience level:', ['Beginner', 'Intermediate'])\n",
    "equipment = st.multiselect('What equipment do you have available?', df['Equipment'].unique(), default=['Bodyweight'])\n",
    "\n",
    "# --- GENERATE AND DISPLAY WORKOUT ---\n",
